import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { useSpring, animated } from 'react-spring';

const calc = (x, y) => [-(y - window.innerHeight / 2) / 20, (x - window.innerWidth / 2) / 20, 1.1];
const trans = (x, y, s) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`;

const AnimatedHeading = () => {
  const [props, set] = useSpring(() => ({
    xys: [0, 0, 1],
    config: { mass: 5, tension: 350, friction: 40 },
  }));
  return (
    <animated.h1
      className="home-heading"
      onMouseMove={({ clientX: x, clientY: y }) => set({ xys: calc(x, y) })}
      onMouseLeave={() => set({ xys: [0, 0, 1] })}
      style={{ transform: props.xys.to(trans) }}
    >
      Hi my name is Mohd Atif!!!
    </animated.h1>
  );
};

const Home = () => {
  return (
    <Container fluid className="home-container">
      <Row className="justify-content-center text-center py-5">
        <Col xs={11} sm={10} md={8} lg={6}>
          <AnimatedHeading />
          <h3 className="home-subheading">I am a Full Stack Developer.</h3>
          <p className="home-description">
            I'm passionate about everything related to the server side.<br />
            I even bought a shirt with a Java icon on it.
          </p>
          <div className="social-icons pt-4">
            <a className="social-icon" href="https://www.linkedin.com/in/mohd-atif-569808278/">
              <i className="fab fa-linkedin-in"></i>
            </a>
            <a className="social-icon" href="https://github.com/Atif2292/">
              <i className="fab fa-github"></i>
            </a>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
